import { useEffect, useMemo, useState } from "react";
import "./app.css"
import Test from "./Components/Test";
import Timer from "./Components/Timer"
import Home from "./Components/Home";



function App() {

  const [username, setUsername] = useState(null);
  const [questionNumber, setQuestionNo] = useState(1);
  const [stop, setStop] = useState(false)
  const [earned, setEarned] = useState("0 points")

  const data = [{
    id: 1,
    question: "What is Software Engineering? ",
    answers: [
      {
        text: "Designing a software",
        correct: false,
      },
      {
        text: "Testing a software",
        correct: false,
      },
      {
        text: "Application of engineering principles to the design a software",
        correct: true,
      },
      {
        text: "None of the above",
        correct: false,
      },
    ],
  },
  {
    id: 2,
    question: "Who is the father of Software Engineering? ",
    answers: [
      {
        text: "Margaret Hamilton",
        correct: false,
      },
      {
        text: "Watts S. Humphrey",
        correct: true,
      },
      {
        text: "Alan Turing",
        correct: false,
      },
      {
        text: "Boris Beizer",
        correct: false,
      },
    ],
  },
  {
    id: 3,
    question: "What are the features of Software Code? ",
    answers: [
      {
        text: "Simplicity",
        correct: false,
      },
      {
        text: "Accessibility",
        correct: false,
      },
      {
        text: "Modularity",
        correct: true,
      },
      {
        text: "All of the above",
        correct: false,
      },
    ],
  },
  {
    id: 4,
    question: " The Cleanroom philosophy was proposed by _________",
    answers: [
      {
        text: "Linger",
        correct: false,
      },
      {
        text: "Mills",
        correct: false,
      },
      {
        text: "Dyer",
        correct: false,
      },
      {
        text: "All of the Mentioned",
        correct: true,
      },
    ],
  },
  ]
  const pointList = useMemo(()=>
    [
      {id:1, point:"100"},
      {id:2, point:"100"},
      {id:3, point:"100"},
      {id:4, point:"100"},
      {id:5, point:"100"},
      {id:6, point:"100"},
      {id:7, point:"100"},
      {id:8, point:"100"},
      {id:9, point:"100"},
      {id:10, point:"100"},
      {id:11, point:"100"},
    ].reverse(),


  []) 
useEffect(()=>{
  questionNumber >1 && setEarned(pointList.find(m=> m.id === questionNumber - 1).point)

}, [pointList, questionNumber])

  return (
  <div className="app">
    {username ? (
      <></>
    ) : <Home setUsername={setUsername} />}
    <div className="main"> 
    {stop ? (<h1 className="endText"> You got: {earned}</h1>
     ):(
      <>
    <div className="top">
      <div className="timer"> <Timer setStop={setStop} questionNumber={questionNumber}/>
      </div>
    </div>
    <div className="bottom">
       <Test 
       data={data} 
       setStop={setStop} 
       questionNumber= {questionNumber}
       setQuestionNo={setQuestionNo}
       />
       </div>
       </>
       )}
    </div>
    <div className="list">
   <ul className="pointList">
    {pointList.map((m) =>(

    <li className= {questionNumber === m.id ? "pointListItem active" : "pointListItem"}>
      <span className="pointListNumber">{m.id}</span>
      <span className="pointListPoint">{m.point}</span>
      </li>
  ))}
   </ul>
    </div>
  </div>
)
}

export default App;
