import  { useEffect, useState } from 'react'
import axios from "axios";

export default function Test({
    data,
    setStop,
    questionNumber, 
    setQuestionNo 
}) {
    const [question, setQuestion] = useState(null)
    const [selectedAnswer, setSelectedAns] = useState(null)
    const [className, setClassName] = useState(null)

    useEffect(()=>{
setQuestion(data[questionNumber-1])
    }, [data, questionNumber])

    const delay = (duration, callback) =>{
        setTimeout(()=>{
            callback()
        }, duration)

    }

    const handleClick = (a) => {
        setSelectedAns(a)
        setClassName("answer active");
        delay(3000, ()=>
    setClassName(a.correct ? "answer correct" : "answer wrong")
);
delay(5000, ()=>
    {
        if(a.correct){
            setQuestionNo((prev) => prev + 1);
            setSelectedAns(null)
        } else{
            setStop(true)
        }
    }
);
    };

    const [datas, setData] = useState();

  useEffect(() => {
    axios.get('"http://localhost:3000/api/test')
      .then((response) => {
        setData(response.datas);
      })
      .catch((error) => {
        console.error(error);
      });
  }, []);
  
  {datas?.map((datas) =>{

  return(
    
    <div className='test ' key={datas.id}>
        
        <div className="question">{datas.question} {question?.question}</div>
      <div className="answers"> {datas.answers}
        {question?.answers.map(a=>(
             <div className={selectedAnswer === a ? className : "answer"}
              onClick={()=>handleClick(a)}> {datas.text}
                {a.text}
                </div>

        ))}
        
      </div>
    </div>
  )
})}
}
